import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class MessagesService {

  public message:string = ""


///////// use material sneak bar instead

  constructor() { }

  public showMessage(msg:string):void{
    this.message = msg;
    setTimeout(() => {
      this.message = "";
    }, 2000);
  }

}
