  templateUrl: './messages-area.component.html',
  styleUrls: ['./messages-area.component.css']
})
export class MessagesAreaComponent implements OnInit {

  constructor(
    public msg:MessagesService
  ) { }

  ngOnInit(): void {
  }


}
